    isSignIn: false,
    name: '',
    email: '',
    password: '',
    photo: ''
  });
  const auth = getAuth();
  const provider = new GoogleAuthProvider();
  const handleSignIn = () => {
    signInWithPopup(auth, provider)
    .then((result) => {
      const { displayName, photoURL, email} = result.user;
      const signInUser = {
        isSignIn: true,
        name: displayName,
        email: email,
        photo: photoURL
      }
      setUser(signInUser);
    })
    .catch((error) => {
      console.log(error);
      console.log(error.message);
    });

  }
  const handleSignOut = () => {
    const auth = getAuth();
signOut(auth).then(() => {
  // Sign-out successful.
  const signOutUser = {
    isSignIn: false,
    name: '',
    email: '',
    photo: ''
  }
  setUser(signOutUser)
}).catch((error) => {
  // An error happened.
  console.log(error);
});
  }
const handleSubmit = () => {

}
const handleBlur = (e) => {
 let isFormValid;
  if(e.target.name === 'email'){
    isFormValid = /\S+@\S+\.\S+/.test(e.target.value);

  }
  if(e.target.name === 'password'){
    const isPasswordValid = /^[a-zA-Z0-9!@#$%^&*]{6,16}$/.test(e.target.value);
    const passwordHasNumber = /\d{1}/.test(e.target.value);

    isFormValid = isPasswordValid && passwordHasNumber;
  }
  if(e.target.name === 'name'){

  }
  if(isFormValid){
    const newUserInfo = {...user};
    newUserInfo[e.target.name] = e.target.value;
    setUser(newUserInfo); 
  }
}
	return <div className="App">
    {
      user.isSignIn? <button onClick={handleSignOut}>Sign Out</button> : 
      <button onClick={handleSignIn}>Sign In</button>
    }
    {
      user.isSignIn && <div>
        <p>Welcome, {user.name}</p>
        <p>Your email: {user.email}</p>
        <img src={user.photo} alt="" />
      </div>
    }
    <h1>Our Own Authentication</h1>
    <p>E-mail: {user.email}</p>
    <p>Password: {user.password}</p>
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder='Write your name here' required /> <br />
    <input type="text" onBlur={handleBlur} name='email' placeholder='Your E-mail' required /> <br />
    <input type="password" onBlur={handleBlur} name="password" placeholder='Your Password' required /> <br />
    <input type="submit" value="Submit" /> 
    </form>
  </div>;
}

export default App;
